import React from 'react'

import Login from './Login'
import Signup from './Signup'
import Welcome from './Welcome'

import Adapter from '../utils/Adapter'

export default class LandingPage extends React.Component {
    state = {
        currentView: '',
    }

    handleChange = (e) => this.setState({ [e.currentTarget.name]: e.currentTarget.value })

    // Test of resuable entry form submission for login & signup
    submitEntryForm = (e, endpoint) => {
          e.preventDefault();
          const state = this.state
          debugger
          Adapter.post(endpoint, { ...this.state })
            .then(res => res.json())
            .then(json => {
                console.log(json)
              // Note: ARCHITECTURE DECISION NEEDED. Should redux be used for project? See Trello board.
            })
            .catch(console.log);
    }

    pageToRender = () => {
        switch (this.state.currentView) {
            case 'login':
                return <Login handleChange={this.handleChange} />
            case 'signup':
                return <Signup handleChange={this.handleChange} />
            default:
                return <Welcome handleChange={this.handleChange} />
        }
    }

    render(){
        return (
            <div>
                {this.pageToRender()} 
            </div>

        )
    }
}
