export default class Adapter {

    static REQUEST_CONFIG = {
        headers: {
            'Accepts': 'application/json',
            'Content-Type': 'application/json'
        },
    }

    static post(endpoint, body) {

        // Determine whether JWT should be included in request
        let headers = this.REQUEST_CONFIG
        if (endpoint !== 'login' && endpoint !== 'signup') {headers.jwt = window.sessionStorage.jwt}

        return fetch(this.REQUEST_CONFIG.BACKEND_URL + endpoint, {
            method: 'POST',
            headers,
            body
        })
    }
}
