import { FETCHED_SPELLS, SEARCH_SPELLS } from "../actionTypes";

export const spellsReducer = (prevState = [], action) => {
  switch (action.type) {
    case SEARCH_SPELLS:
      return action.spellQuery
    case FETCHED_SPELLS:
        return action.spells
    default:
      return prevState;
  }
};
