export default class Adapter {

    BACKEND_URL = 'http://localhost:3000/'
    HEADERS = {
                'Accepts': 'application/json',
                'Content-Type': 'application/json'
            }

    static post(endpoint, body) {
        fetch(this.BACKEND_URL + endpoint, {
            headers: this.HEADERS,
            body
        })
    }
}
